import styled from 'styled-components'

export const Title = styled.h1`
  color: skyblue;
  font-size: ${props => props.primary ? "2em" : "1em"};
  font-family: ${({theme}) => theme.fonts.titleFont.family};
  text-align: center;
  @media only screen and (max-width: 800px) {
    text-align: left;
    font-style: italic;
  }
`

export const Subtitle = styled(Title)`
  font-size: "1em";
  color: peru
`

export const Centered = styled.h3`
  text-align: center; 
`

export const bt = styled.h5`
  .button {
    display: inline-block;
    border-radius: 4px;
    background-color: #f4511e;
    border: none;
    color: #FFFFFF;
    text-align: center;
    font-size: 28px;
    padding: 20px;
    width: 200px;
    transition: all 0.5s;
    cursor: pointer;
    margin: 5px;
  }

  .button span {
    cursor: pointer;
    display: inline-block;
    position: relative;
    transition: 0.5s;
  }

  .button span:after {
    content: '\00bb';
    position: absolute;
    opacity: 0;
    top: 0;
    right: -20px;
    transition: 0.5s;
  }

  .button:hover span {
    padding-right: 25px;
  }

  .button:hover span:after {
    opacity: 1;
    right: 0;
  }
`

export const a = styled.h6`
  color: hotpink
`
