import styled from "styled-components";

export const GTitle = styled.h1`
 text-align: center; 
 color: coral; 
 font-size: 2em;
 font-family: sans-serif;
` 

export const GSubtitle =styled.h2`
 text-align: center; 
 color: darkorange; 
 font-size: 1.2em; 
 font-family: sans-serif;
`

export const GPython = styled.h3`
 text-align: start; 
 color: goldenrod; 
 font-family: sans-serif;
 padding-left: 30px; 
`
